package com.pcl.procastlive;import android.app.Activity;import android.content.Intent;import android.media.MediaPlayer;import android.net.Uri;import android.os.Bundle;import android.os.Handler;import android.view.MotionEvent;import android.view.WindowManager;import android.widget.VideoView;import androidx.appcompat.app.AppCompatActivity;public class SplashActivity extends AppCompatActivity {    VideoView videoView ;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_splash);        videoView = (VideoView) findViewById(R.id.videoView);        getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN,WindowManager.LayoutParams.FLAG_FULLSCREEN);        Uri video = Uri.parse("android.resource://" + getPackageName() + "/" + R.raw.splash);        videoView.setVideoURI(video);        videoView.setOnCompletionListener(new MediaPlayer.OnCompletionListener() {            public void onCompletion(MediaPlayer mp) {                startNextActivity();            }        });        videoView.start();    }    private void startNextActivity() {        if (isFinishing())            return;        startActivity(new Intent(this, LoginActivity.class));        finish();    }}